import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';

import { withStyles } from '@material-ui/core/styles';

import CopyRight from './../components/CopyRight'

import {fetch as fetchPolyfill} from 'whatwg-fetch'

import {FetchDoGet,FetchDoPost} from './../commons/Utils'
import Dialog from './../commons/DialogUtils'

import $ from 'jquery'

const style ={
  paper: {
    marginTop: "30px",
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: "2px",
    backgroundColor: "#fff",
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: "2px",
  },
  submit: {
    margin: "8px",
    height:"50px"

  },
  title:{
      fontSize:"36px",
      color:"#64b5f6",
      margin:"10px auto"
  },
  verifyCodeDiv:{
    height:"50px"
  }
  ,
  verifyCode:{
    cursor:"pointer",
    position:"relative",
    right:"-32%",
    bottom:"50px",
    margin:"2px 9px",
    textAlign:"left"

  }
};



function veryfyCode(options){
  console.log(options);
}

/**
 * 我们的登陆的基本操作
 */
class LoginMod extends React.Component{
  constructor(props){
    super(props)
    this.state={
      src: this.props.src ? this.props.src : '',
      verifyCodeKey:''
    }
    this.refreshVerifyCode=this.refreshVerifyCode.bind(this)
    this.preSubmit=this.preSubmit.bind(this)
  }

  refreshVerifyCode(){
    const _this=this;
    FetchDoGet("http://api.breakpoint.vip/block/v1/image/getVerifyCode",function(response){
      _this.setState({
        src: response.verifyCode,
        verifyCodeKey:response.verifyCodeKey
    })
      console.log("response",response);
    });
  }

  preSubmit(e) {  
    console.log(e)
    //e.target;
    console.log(e.target);

    let requestData={
      verifyCodeKey:this.state.verifyCodeKey,
      verifyCode:$("#verifyCode").val()
    }

    const username=$("#username").val();
    const password=$("#password").val();

    FetchDoPost("http://api.breakpoint.vip/block/v1/sys_user/login/"+username+"/"+password,function(response) {
      console.log(response);
    },requestData);
    /**
     * 阻止提交我们的提交
     */
    e.preventDefault();
  }

  componentDidMount(){
    const _this=this;
    FetchDoGet("http://api.breakpoint.vip/block/v1/image/getVerifyCode",function(response){
      _this.setState({
        src: response.verifyCode,
        verifyCodeKey:response.verifyCodeKey,
        open:true,
        message:'',
    })
    console.log("response",response);
    },{'key1':'value',"verifyCodeKey":"ssddd"});
  }

  render(){
    const {classes} = this.props;

    let {src,open,message} = this.state;

    return(
<Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
      <div className={classes.title}>基于区块链的光网络建设管理平台</div>
        {/* <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar> */}
        {/* <Typography component="h1" variant="h5">
          Sign in
        </Typography> */}
        <form className={classes.form} noValidate onSubmit={this.preSubmit}>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="username"
            label="用户名"
            name="username"
            autoComplete="username"
            autoFocus
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="密码"
            type="password"
            id="password"
            autoComplete="current-password"
          />
          <div>
          <TextField id="verifyCode"
           label="验证码"
           required
           fullWidth
            variant="outlined" />
          <img id="" src={src} className={classes.verifyCode} onClick={this.refreshVerifyCode}/>
          </div>

          {/* <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          /> */}
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            登陆
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              <Link href="#" variant="body2">
                {"Don't have an account? Sign Up"}
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={8}>
        <CopyRight />
      </Box>
      <Dialog open={open} message={message}/>
    </Container>
    )
  }
}

export default withStyles(style)(LoginMod);





