import React from 'react'
import './Album.css'
import { withRouter } from 'react-router-dom'
import {Row,Col, Card,List} from 'antd'
import BlogCommonBackGround from '../../components/blog/BlogCommonBackGround'
import { FetchDoGet } from '../../commons/Utils'
import { EnvironmentOutlined } from '@ant-design/icons';



const getter=[16,24];
const colSpan=6;
/**
 * 我们的相册的页
 */
class Album extends React.Component{

    constructor(props){
        super(props)

        this.state={
            requestUrl:"/blog/v1/album/selectPhotos",
            photoPageInfo:{},
            photos:[],
            first:{}
        }
    }

    /**
     * 初始化我们的基本的数据
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet(this.state.requestUrl,(response)=>{
            console.log(response)
            this.setState({
                photoPageInfo:response,
                photos:response.data,
                first:response.data[0]
            })
        })

    }

    render(){

        return(
            <BlogCommonBackGround
            leftSpan={0}
            rightSpan={24}
            extra={
                <div>
                    <Row getter={[16,24]} style={{margin:"24px auto"}}>
                        <Col span={24}>
                            <Card 
                                hoverable
                                cover={<img alt="example" src={this.state.first.aPhoto}/>}
                                // title={item.aLocaltion}
                                >
                                <Card.Meta title={
                                    <span>
                                        <EnvironmentOutlined />
                                        {` `+this.state.first.aLocaltion}
                                    </span>

                                    } description={
                                    <div>
                                        <span style={{fontWeight:"bold",color:"black"}}>{this.state.first.gmtUpdate+` `}</span>
                                        {this.state.first.aMsg}
                                    </div>
                                } />
                                </Card>
                        </Col>
                     </Row>
                     <Row getter={getter}>
                         {this.state.photos.map((item)=>{
                             return(
                                <Col span={6}>
                                    <Card 
                                    hoverable
                                    cover={<img alt="example" src={item.aPhoto}/>}
                                    // title={item.aLocaltion}
                                    >
                                    <Card.Meta title={
                                        <span>
                                            <EnvironmentOutlined />
                                            {` `+item.aLocaltion}
                                        </span>

                                        } description={
                                        <div>
                                            <span style={{fontWeight:"bold",color:"black"}}>{item.gmtUpdate+` `}</span>
                                            {item.aMsg}
                                        </div>
                                    } />
                                    </Card>
                                </Col>
                             )
                         })}
                     </Row>
                        <List
                            grid={{ gutter: [16, 24], column: 4, }}
                            dataSource={this.state.photos}
                            renderItem={item => (
                            <List.Item>
                                <Card 
                                hoverable
                                cover={<img alt="example" src={item.aPhoto}/>}
                                // title={item.aLocaltion}
                                >
                                <Card.Meta title={
                                    <span>
                                        <EnvironmentOutlined />
                                        {` `+item.aLocaltion}
                                    </span>

                                    } description={
                                    <div>
                                        <span style={{fontWeight:"bold",color:"black"}}>{item.gmtUpdate+` `}</span>
                                        {item.aMsg}
                                    </div>
                                } />
                                </Card>
                            </List.Item>
                            )}
                        />

                </div>
            }
            />
        )
    }
}

export default withRouter(Album)
