import React from 'react'

//基本的公共导入模块 start
import './../commons/layoutcom.css'
import HeaderPrivate from './../components/HeaderPrivate'
import Bottom from './../components/Bottom'
import { withRouter } from 'react-router-dom'
import 'antd/dist/antd.css';
import {Layout,PageHeader,Divider,Breadcrumb} from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  ArrowLeft,
  LeftOutlined
} from '@ant-design/icons';
import MenuComponent from './../components/MenuCom'
import {FetchDoGet,FetchDoPost} from './../commons/Utils'

//基本的公共导入模块 end 

//定义常量
const { Header, Sider, Content} = Layout;
/**
 * 我们后台的公共模板
 * 用于后台的所有的页面
 */
class LocalBackGround extends React.Component{
    //构造函数
    constructor(props){
        super(props)
        this.state={
            collapsed: false,
            user:{},
            role:{},
            other:{},
            pageTitle:"",
            pageSubTitle:""
        }
    }
    toggle = () => {
        this.setState({
          collapsed: !this.state.collapsed,
        });
    };

    setTitle=(data)=>{
        //设置我们的标题
        console.log("pageTitle",data);
        this.setState({
            pageTitle:data.title,
            pageSubTitle:data.subTitle
        })

    }
    //初始化我们的数据
    componentDidMount(){
        const _this=this;
        let user_info=localStorage.getItem("user_info");
        //console.log("user_info",user_info)
        if("null"==user_info||null==user_info||undefined==user_info){
            //获取用户的登陆的信息
            FetchDoGet("/block/v1/sys_user/isLogin",function(response){
                localStorage.setItem("user_info",JSON.stringify(response))
                _this.setState({
                    user:response.tbSystemUser,
                    role:response.tbSystemRole,
                    other:response.userOtherMessageInfo
                })
            });  
         }else{
            let userinfo=JSON.parse(user_info);
            if(null!=userinfo){
                _this.setState({
                    user:userinfo.tbSystemUser,
                    role:userinfo.tbSystemRole,
                    other:userinfo.userOtherMessageInfo
                })
            }

         }//end else

    }
    //执行渲染我们的组件
    render(){
        let {user,role,other}=this.state;
        const breadcrumb=this.props.breadcrumb;
        console.log("breadcrumb",breadcrumb)
        return( 
            <div>
                <HeaderPrivate name="zhaoligang" 
                callback={this.toggle} 
                avatar={user.userPhoto}
                />
                    <Layout className="layout-header">
                        <Sider theme="light" trigger={null} collapsible collapsed={this.state.collapsed}>
                        <div className="logo" />
                        <MenuComponent setTitleCallBack={this.setTitle}/>
                        </Sider>
                        <Layout className="site-layout">
                        {/* <Header className="site-layout-background" style={{ padding: 0 }}>
                            {React.createElement(this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
                            className: 'trigger',
                            onClick: this.toggle,
                            })}
                        </Header> */}
                        <Content
                            className="site-layout-background"
                            style={{
                                margin: '4px 5px 0px 3px',
                                padding: "0px",
                                minHeight: 680,
                            }}
                        >
                            <PageHeader title={this.state.pageTitle}
                            subTitle={this.state.pageSubTitle}
                            // footer={<Bottom/>}
                            backIcon={<LeftOutlined />}
                            // onBack={()=>{

                            // }}
                            breadcrumb={<Breadcrumb  routes={breadcrumb} />}
                            >
                                {/* //定义我们自己的组件 */}
                                {this.props.content}
                            </PageHeader>
                        </Content>
                        </Layout>
                    </Layout>
                <Bottom/>
            </div>
        )//end return

    }//end render
}

export default withRouter(LocalBackGround)
