import React from 'react';
// import logo from './logo.svg';
import './App.css';

// import {BrowserRouter, Route} from 'react-router-dom'
import { BrowserRouter as Router,Route,Switch,Link,Redirect, withRouter} from "react-router-dom";
import { connect, Provider } from 'react-redux'

import Routers from './router/routers'

import FileNotFound from './router/blog/FileNotFound'


import zhCN from 'antd/es/locale/zh_CN';
import { ConfigProvider } from 'antd';
const themeList={
  theme1:{

  }
}


class App extends React.Component{
  constructor(props){
    super(props)
  }

  /**
   * 添加预加载的动画
   */
  componentWillMount(){
    let loading = document.getElementById('i-loading')
    if (loading) {
      loading.setAttribute('class', 'i-loading-out')
      setTimeout(() => {
        loading.style.display = 'none'
      }, 1000)
    }
  }

  render(){
    let token=localStorage.getItem("user_token");
    console.log(token)
    return(
      <ConfigProvider locale={zhCN}>
        <Router>
          <div>
            <Switch>
              {Routers.map((item,index)=>{
              return <Route key={index} path={item.path} exact render={props =>
                (!item.auth ? (<item.component {...props} />) : (token ? <item.component {...props} /> : <Redirect to={{
                 pathname: '/login',
                 state: { from: props.location }
                }} />)
               )}/>
              })}
            // 所有错误路由跳转页面
            <Route component={FileNotFound} />
           </Switch>
        </div>
{/*         
            <Route path={`/private`} component={PrivateZone}></Route>
            <Route path={`/login`} component={LoginMod}></Route> */}
        </Router>
      </ConfigProvider>
    )
  }
}

