import React from 'react'
import './UserInfo.css'


import './../commons/layoutcom.css'
import HeaderPrivate from './../components/HeaderPrivate'
import Bottom from './../components/Bottom'
import { withRouter } from 'react-router-dom'
import 'antd/dist/antd.css';
import { Layout,Card,Button,Divider,Table} from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
} from '@ant-design/icons';
import MenuComponent from './../components/MenuCom'
import LocalIconFont from './../components/LocalIconFont'
import {FetchDoGet,FetchDoPost} from './../commons/Utils'

const { Header, Sider, Content} = Layout;

const gridStyle = {
    width: '50%',
    textAlign: 'center',
};


const columns = [
    {
        title: '用户名',
        width: 50,
        dataIndex: 'userName',
        key: 'userName',
        fixed: 'left',
        align:"tenter"
    },
    {
        title: '昵称',
        width: 50,
        dataIndex: 'userNickName',
        key: 'userNickName',
        align:"tenter"
    },
    {
        title: '手机号',
        width: 50,
        dataIndex: 'userPhone',
        key: 'userPhone',
        align:"tenter"
    },
    {
        title: '邮箱',
        width: 80,
        dataIndex: 'userEmail',
        key: 'userEmail',
        align:"tenter"
    },
    {
        title: '部门',
        width: 50,
        dataIndex: 'departName',
        key: 'departName',
        align:"tenter"
    },
    {
        title: '角色',
        width: 50,
        dataIndex: 'roleName',
        key: 'roleName',
        align:"tenter"
    },
    {
        title: '创建时间',
        width: 50,
        dataIndex: 'gmtCreate',
        key: 'gmtCreate',
        align:"tenter"
    },
    {
        title: '操作',
        key: 'operation',
        fixed: 'right',
        align:"tenter",
        width: 70,
        render: (text, record, index) => {
            console.log("row",record);
            return (
                <div style={{height:"40px"}}>
                    <span style={{float:"left",width:"45%",marginLeft:"3px"}}>
                        <Button type="primary"
                        ghost={false} 
                        style={{backgroundColor:"#81c784",border:"none",float:"left"}}
                        icon={<LocalIconFont type="icon-edit" />}
                        >
                        编辑</Button>

                    </span>
                    <span style={{float:"left",width:"45%",marginLeft:"4px"}}>
                    <Button type="danger"
                        ghost={false} 
                        style={{backgroundColor:"#f44336",border:"none",float:"left"}}
                        icon={<LocalIconFont type="icon-edit" />}
                        >
                        冻结</Button>
                    </span>
                </div>
            )
        },
    },
];


class UserInfo extends React.Component{

    constructor(props){
        super(props)
        this.state={
            collapsed: false,
            users:{}
        }
    }

    toggle = () => {
        this.setState({
          collapsed: !this.state.collapsed,
        });
    };

    /**
     * 初始化基本的信息
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet("/block/v1/sys_user/selectAllUsers",function(response){
            console.log("selectAllUsers",response);
            _this.setState({
                users:response
            })
        });

    }

    render(){
        return(
            <div>
                <HeaderPrivate name="zhaoligang" callback={this.toggle}/>
                    <Layout className="layout-header">
                        <Sider theme="light" trigger={null} collapsible collapsed={this.state.collapsed}>
                        <div className="logo" />
                        <MenuComponent/>
                        </Sider>
                        <Layout className="site-layout">
                        {/* <Header className="site-layout-background header-class" style={{ padding: 0 }}>
                            {React.createElement(this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
                            className: 'trigger',
                            onClick: this.toggle,
                            })}
                        </Header> */}
                        <Content
                            className="site-layout-background"
                            style={{
                            margin: '5px 5px',
                            padding: 24,
                            minHeight: 580,
                            }}
                        >
                            <Card style={{width:"100%"}} 
                            bordered={false}
                            hoverable={true}
                            title="用户信息列表"
                            extra={
                                <div>
                                    <Button type="danger" 
                                    style={{float:"right",marginRight:"4px"}}>
                                        批量冻结
                                    </Button>
                                    <Button type="primary" style={{float:"right",marginRight:"4px"}}>添加用户</Button>
                                    <Button type="primary" style={{float:"right",marginRight:"4px"}}>查询</Button>  
                                </div>
                            }
                            >

                                <Table columns={columns} 
                                dataSource={this.state.users.data} 
                                scroll={{ x: 1500, y: 440 }}
                                size="middle"
                                align="center"
                                />,

                            </Card>

                            {/* <Divider dashed={true}/>

                            <Card style={{width:"100%"}} 
                            bordered={false}
                            hoverable={true}
                            title="个人资产信息"
                            extra={<Button type="primary" style={{float:"right"}}>查看详情</Button>}
                            >

                            </Card> */}
                        </Content>
                        </Layout>
                    </Layout>
                <Bottom/>
            </div>
        )
    }
}
export default UserInfo
