import React from 'react'
import './CommentSite.css'
import { withRouter } from 'react-router-dom'
import {List,Card,Comment,Pagination,Tag,Row,Col,Form,Input,Button,Collapse} from 'antd'
import BlogCommonBackGround from '../../components/blog/BlogCommonBackGround'
import { FetchDoGet, FetchDoPost } from '../../commons/Utils'
import moment from 'moment';
import { TextField } from '@material-ui/core'
import PorpularArticle from '../../components/blog/PorpularArticle'
import CommentPanel from '../../components/blog/CommentPanel'


/**
 * 我们的留言区域
 */
const { TextArea } = Input;
/**
 * 我们的留言版本
 */
class CommentSite extends React.Component{

    /**
     * 我们的表单的操作
     */
    formRef = React.createRef();

    constructor(props){
        super(props)
        this.state={
            veryfyCode:"",
            verifyCodeKey:"",
            pictureUrl:"http://file.breakpoint.vip/picture/blog/4d6fb840-b0b5-46ed-9db8-98084c585229.jpeg",                   
            commentpanelkey:Math.random()

        }
    }

    /**
     * 初始化的数据
     */
    componentDidMount(){
        const _this=this;

        FetchDoGet("/blog/v1/image/getKaptcha",(response)=>{
            _this.setState({
                veryfyCode:response.verifyCode,
                verifyCodeKey:response.verifyCodeKey
            })
        })

    }

    render(){
        console.log("this.formRef.current",this.formRef.current)
        return(
            <BlogCommonBackGround
            left={
                <Row gutter={[16,24]}>
                    <Col span={24}>
                        <Card title={"感谢支持"}>
                            <h4>您的支持是给我最大的鼓励!!!!!</h4>
                        </Card>
                    </Col>
                    <Col span={24}>
                        <PorpularArticle
                        title={"文章推荐"}
                        />
                    </Col>
                </Row>
            }
            extra={
                <Row gutter={[16,24]}>
                    <Col span={24}>
                        <Card
                        cover={
                            <img src="https://myblogadmin.breakpoint.vip/image/liuyan.jpg"/>
                        }
                        >
                            <Collapse 
                            bordered={false}
                            onChange={()=>{}}>
                                <Collapse.Panel header={
                                    <div>
                                        <span>点击我，留下对我想说的话<Tag style={{marginLeft:"10px"}} color="#fa541c">here</Tag></span>
                                    </div>
                                } key="1">
                                <div className="comments-form-outer">
                                    <Form 
                                    // layout={"vertical"}
                                    size={"middle"}
                                    ref={this.formRef} 
                                    name={"comment-form"} 
                                    onFinish={(values)=>{
                                        console.log(values)
                                        const _this=this;
                                        values['verifyCodeKey']=this.state.verifyCodeKey;
                                        values['pictureUrl']=this.state.pictureUrl;
                                        FetchDoPost("/v1/liuyan/save",(response)=>{
                                            //console.log(this.formRef.current)
                                            _this.formRef.current.setFieldsValue({
                                                message:"",
                                                userName:"",
                                                verifyCode:""
                                            })
                                            _this.setState({
                                                commentpanelkey:Math.random()
                                            })
                                        },values)


                                    }}>
                                        <Form.Item 
                                        name="message" 
                                        // label="留言信息" 
                                        // help={"您的留言信息"}
                                        rules={[{ 
                                            required: true ,
                                            message:"请输入留言信息并且字数在5-200之间",
                                            max:200,
                                            min:5
                                        }]}>
                                            <TextField
                                            fullWidth
                                            multiline
                                            rowsMax="4"
                                            variant="outlined" 
                                            label="留言信息"
                                            placeholder={"请输入留言信息"}
                                            allowClear={true}
                                            autoSize={{ minRows: 2, maxRows: 6 }}
                                            />
                                        </Form.Item>
                                        <Form.Item 
                                        name="userName" 
                                        // label="你的称呼"
                                        rules={[{ 
                                            required: true ,
                                            message:"请输入您的称呼并且字数在1-15之间",
                                            max:15,
                                            min:1
                                            }]}>
                                            <TextField
                                            fullWidth
                                            label="您的称呼"
                                            placeholder="请输入您的称呼"
                                            />

                                        </Form.Item>
                                        <Form.Item 
                                        name="verifyCode" 
                                        // label="验证码" 
                                        rules={[{ 
                                            required: true ,
                                            message:"请输入验证码",
                                            }]}>
                                            <Input 
                                            placeholder="请输入验证码"
                                            suffix={
                                                <img style={{cursor:"pointer"}} src={this.state.veryfyCode}
                                                onClick={()=>{
                                                    const _this=this;
                                                    FetchDoGet("/blog/v1/image/getKaptcha",(response)=>{
                                                        _this.setState({
                                                            veryfyCode:response.verifyCode,
                                                            veryfyCodeKey:response.verifyCodeKey
                                                        })
                                                    })
                                                }}
                                                />
                                            }
                                            />
                                        </Form.Item>
                                        <Form.Item>
                                            <Button type="primary" htmlType="submit">
                                                发表
                                            </Button>
                                        </Form.Item>

                                    </Form>
                                </div>
                                </Collapse.Panel>
                            </Collapse>

                        </Card>
                    </Col>
                    <Col span={24}>
                        <CommentPanel
                        key={this.state.commentpanelkey}
                        />
                    </Col>
                </Row>

            }
            />
        )
    }

}

export default withRouter(CommentSite)
