import React from 'react'
import './ManageUsers.css'
import LocalBackGround from './../commons/LocalBackGround'
import {FetchDoGet,FetchDoPost} from './../commons/Utils'
import { withRouter } from 'react-router-dom'
import 'antd/dist/antd.css';

import { Layout,Card,Button,Divider,Table,Pagination} from 'antd';
import LocalIconFont from '../components/LocalIconFont'

import TableUtils from './../components/TabbleUtils'

const columns = [
    {
        title: '用户名',
        width: 50,
        dataIndex: 'userName',
        key: 'userName',
        fixed: 'left',
        align:"tenter"
    },
    {
        title: '昵称',
        width: 50,
        dataIndex: 'userNickName',
        key: 'userNickName',
        align:"tenter"
    },
    {
        title: '手机号',
        width: 50,
        dataIndex: 'userPhone',
        key: 'userPhone',
        align:"tenter"
    },
    {
        title: '邮箱',
        width: 80,
        dataIndex: 'userEmail',
        key: 'userEmail',
        align:"tenter"
    },
    {
        title: '部门',
        width: 50,
        dataIndex: 'departName',
        key: 'departName',
        align:"tenter"
    },
    {
        title: '角色',
        width: 50,
        dataIndex: 'roleName',
        key: 'roleName',
        align:"tenter"
    },
    {
        title: '创建时间',
        width: 50,
        dataIndex: 'gmtCreate',
        key: 'gmtCreate',
        align:"tenter"
    },
    {
        title: '操作',
        key: 'operation',
        fixed: 'right',
        align:"tenter",
        width: 70,
        render: (text, record, index) => {
            console.log("row",record);
            return (
                <div style={{height:"40px"}}>
                    <span style={{float:"left",width:"45%",marginLeft:"3px"}}>
                        <Button type="primary"
                        ghost={false} 
                        style={{backgroundColor:"#81c784",border:"none",float:"left"}}
                        icon={<LocalIconFont type="icon-edit" />}
                        >
                        编辑</Button>

                    </span>
                    <span style={{float:"left",width:"45%",marginLeft:"4px"}}>
                    <Button type="danger"
                        ghost={false} 
                        style={{backgroundColor:"#f44336",border:"none",float:"left"}}
                        icon={<LocalIconFont type="icon-edit" />}
                        >
                        冻结</Button>
                    </span>
                </div>
            )
        },
    },
];


class ManageUsers extends React.Component{

    constructor(props){
        super(props)
        this.state={
            users:{}
        }
    }
    /**
     * 初始化基本的信息
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet("/block/v1/sys_user/selectAllUsers",function(response){
            console.log("selectAllUsers",response);
            _this.setState({
                users:response
            })
        });

    }

    render(){
        return(
            <LocalBackGround content={
                <div>
                    <Card style={{width:"100%"}} 
                            bordered={false}
                            hoverable={false}
                            title="用户信息列表"
                            extra={
                                <div>
                                    <Button type="danger" 
                                    style={{float:"right",marginRight:"4px"}}>
                                        批量冻结
                                    </Button>
                                    <Button type="primary" style={{float:"right",marginRight:"4px"}}>添加用户</Button>
                                    <Button type="primary" style={{float:"right",marginRight:"4px"}}>查询</Button>  
                                </div>
                            }
                    >
                        <TableUtils columns={columns}
                        requestUrl={"ddd"}
                        currentPage={1}
                        />

                                {/* <Table columns={columns} 
                                dataSource={this.state.users.data} 
                                scroll={{ x: 1500, y: 440 }}
                                size="small"
                                align="center"

                                />,
                                <Pagination 
                                    showSizeChanger
                                    total={300}
                                    /> */}
                     </Card>
                </div>
            }/>
        )
    }
}
export default withRouter(ManageUsers)
