import React from 'react'
import './StartPage.css'
import { withRouter } from 'react-router-dom'
import BlogCommonBackGround from '../../components/blog/BlogCommonBackGround'
import { Card, Col, Row,List, Divider,Calendar,Avatar,Button,Tag, Breadcrumb } from 'antd';
import ArticleBar from '../../components/blog/ArticleBar';
import { FetchDoGet } from '../../commons/Utils';
import { EditOutlined, EllipsisOutlined, SettingOutlined } from '@ant-design/icons';
import LocalIconFont from '../../components/LocalIconFont';

/**
 * 我们的首页
 */
class StartPage extends React.Component{

    constructor(props){
        super(props)

        this.state={
            hotArticles:[]
        }
    }

    /**
     *初始化我们的数据
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet("/blog/v1/topic/getTop3Topic",function(response){
            console.log(response)
            _this.setState({
                hotArticles:response
            })
        })

        // FetchDoGet("/blog/v1/topic/getTop3Topic",function(response){

        // })

    }

    render(){

        return(
            <BlogCommonBackGround
            leftSpan={0}
            rightSpan={24}
            rightOffset={0}
            extra={
                <div>
                    <Row gutter={[16, 24]}>
                        <Col span={12}>
                            <Card  bordered={false}>
                                <ArticleBar/>
                            </Card>
                        </Col>
                        <Col span={12}>
                            <Row gutter={[16, 24]}>
                                <Col span={24}>
                                    <Card  bordered={false}>
                                        <div className="site-calendar-demo-card">
                                            <Calendar fullscreen={false} onPanelChange={()=>{}} />
                                        </div>
                                    </Card>
                                </Col>
                                <Col span={24}>
                                    <Card 
                                    title={"今日天气"} bordered={false}>
                                        天气的模块
                                    </Card>
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                    <Row gutter={[16, 24]}>
                        <Col span={24}>
                            <Card className="porpular-aticles" bordered={false}>
                                最受欢迎文章
                            </Card>
                        </Col>
                    </Row>
                    <Row gutter={[16, 24]}>

                    <List
                    itemLayout="vertical"
                    size="large"
                    grid={{ gutter: 16, column: 2}}
                    pagination={false}
                    dataSource={this.state.hotArticles}
                    footer={
                        ""
                    }
                    renderItem={(item)=>(
                        <List.Item
                        key={item.topicTitle}

                        >
                            <List.Item.Meta
                                // avatar={<Avatar src={item.photoPath} />}
                                />
                                    <Card
                                    className="porpular-aticles"
                                    title={
                                        <span>
                                            <Avatar src="http://file.breakpoint.vip/picture/blog/4f6bbb5c-2404-484a-a9f1-062317bc840e.jpg" />
                                            <a style={{marginLeft:"8px"}} href={`/tech-article?topicId=`+item.topicId}>
                                                <span style={{fontSize:"18px"}}>{item.topicTitle}</span>
                                            </a>
                                        </span>
                                    }
                                    hoverable={true}
                                    bordered={true}>

                                        {/* {item.topicDesc} */}
                                    <Card.Meta
                                    // avatar={}
                                    // title={
                                    //     <a href={`/tech-article?topicId=`+item.topicId}>
                                    //         <span style={{fontSize:"18px"}}>{item.topicTitle}</span>
                                    //     </a>
                                    // }
                                    description={
                                        <div>
                                            <Tag color="blue">{item.topicCategory}</Tag>
                                            {item.topicDesc}
                                            <Button type="link"
                                            href={`/tech-article?topicId=`+item.topicId}
                                            >阅读更多</Button>
                                        </div>
                                    }
                                    />
                                    <Breadcrumb
                                    style={{marginTop:"24px"}}
                                     separator={" "}>
                                        <Breadcrumb.Item>
                                            <span style={{marginRight: "8px"}}>
                                                <LocalIconFont type="icon-information"/>&nbsp;
                                                {item.nickName}
                                            </span>
                                        </Breadcrumb.Item>
                                        <Breadcrumb.Item>
                                            <span style={{marginRight: "8px"}}>
                                                <LocalIconFont type="icon-see"/>&nbsp;
                                                {item.seeCount}人已围观
                                            </span>
                                        </Breadcrumb.Item>
                                        <Breadcrumb.Item>
                                            <span style={{marginRight: "8px"}}>
                                                <LocalIconFont type="icon-riqi2"/>&nbsp;
                                                {item.gmtModified}
                                            </span>
                                        </Breadcrumb.Item>
                                    </Breadcrumb>
                                </Card>
                            </List.Item>   
                      )}
                    />
                    </Row>
                </div>
            }
            />
        )
    }
}

export default withRouter(StartPage)
