import React from 'react'
import { withRouter } from 'react-router-dom'
import {FetchDoGet, FetchDoPost} from './../commons/Utils'
import { Select } from 'antd';

const { Option } = Select;

/**
 * 我们的部门的下啦列表框
 */
class DepartSelect extends React.Component{

    constructor(props){
        super(props)
        this.state={
            departs:[]
        }
    }

    /**
     * 初始化我们的数据
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet("/block/v1/depart/selectDeparts",function(response){
            console.log("selectDeparts",response);
            _this.setState={
                departs:response
            }
        })

    }//end

    handleChange=(value)=>{
        console.log(`selected ${value}`);
    }

    render(){
        return(
            <div>
                <Select style={{ width: 120 }}>
                    {this.state.departs.map((item,index)=>{
                        return <Option value="jack">Jack</Option>
                    })}
                </Select>
            </div>
        )//end return
    }//end render
}//end

export default withRouter(DepartSelect)
