import React from 'react'
import './CommentSite.css'
import { withRouter } from 'react-router-dom'
import {List,Card,Comment,Pagination,Tag,Row,Col} from 'antd'
import BlogCommonBackGround from '../../components/blog/BlogCommonBackGround'
import { FetchDoGet } from '../../commons/Utils'
import moment from 'moment';
import PorpularArticle from '../../components/blog/PorpularArticle'

/**
 * 我们的留言版本
 */
class CommentSite extends React.Component{

    constructor(props){
        super(props)
        this.state={
            requestUrl:"/v1/liuyan/getYouLiuYanList",
            comments:[],
            pageInfo:{}

        }
    }

    /**
     * 初始化的数据
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet(this.state.requestUrl,(response)=>{
            _this.setState({
                comments:response.data,
                pageInfo:response
            })
        })

    }

    render(){

        return(
            <BlogCommonBackGround
            left={
                <Row gutter={[16,24]}>
                    <Col span={24}>
                        <Card title={"感谢支持"}>
                            <h4>您的支持是给我最大的鼓励!!!!!</h4>
                        </Card>
                    </Col>
                    <Col span={24}>
                        <PorpularArticle
                        title={"文章推荐"}
                        />
                    </Col>
                </Row>
            }
            extra={
                <Row gutter={[16,24]}>
                    <Col span={24}>
                        <Card
                        cover={
                            <img src="https://blog.breakpoint.vip/image/liuyan.jpg"/>
                        }
                        >
                            <p>功能待续</p>

                        </Card>
                    </Col>
                    <Col span={24}>
                        <Card
                        title={"留言板"}
                        >
                            <List
                                className="comment-list"
                                // header={`${this.state.comments.length} replies`}
                                itemLayout="horizontal"
                                dataSource={this.state.comments}
                                pagination={false}
                                footer={
                                    <Pagination 
                                    showQuickJumper
                                    showTotal={(total, range)=>{
                                        return `当前显示：${range[0]}-${range[1]} 文章 总计 ${total} 篇文章`
                                    }}
                                    current={this.state.pageInfo.currentPage}
                                    defaultPageSize={this.state.pageInfo.pageSize}
                                    defaultCurrent={this.state.pageInfo.currentPage} 
                                    total={this.state.pageInfo.totalCount} 
                                    onChange={(page, pageSize)=>{
                                        const _this=this;
                                        FetchDoGet(this.state.requestUrl,function(response){
                                            console.log(response)
                                            _this.setState({
                                                comments:response.data,
                                                pageInfo:response
                                            })
                                        },{
                                            currentPage:page,
                                            pageSize:pageSize
                                        });
                                    }} />
                                }
                                renderItem={item => (
                                <li>
                                    <Comment
                                    // actions={item.actions}
                                    author={
                                        <span style={{fontSize:"14px",fontWeight:"bold"}}>
                                            {item.userName}
                                        </span>
                                    }
                                    avatar={item.pitureUrl}
                                    content={
                                    <span style={{fontSize:"14px"}}>{item.message}</span>
                                    }
                                    datetime={
                                        <span style={{fontSize:"14px",fontWeight:"bold"}}>
                                            ({item.gmtUpdate})
                                        </span>

                                    }
                                    />
                                </li>
                                )}
                            />
                        </Card>
                    </Col>
                </Row>

            }
            />
        )
    }

}

export default withRouter(CommentSite)
