import React from 'react'
import './StartPage.css'
import { withRouter } from 'react-router-dom'
import BlogCommonBackGround from '../../components/blog/BlogCommonBackGround'
import { Card, Col, Row,List, Divider,Calendar,Avatar,Button,Tag, Breadcrumb } from 'antd';
import ArticleBar from '../../components/blog/ArticleBar';
import { FetchDoGet } from '../../commons/Utils';
import { EditOutlined, EllipsisOutlined, SettingOutlined } from '@ant-design/icons';
import LocalIconFont from '../../components/LocalIconFont';
import PublicLeft from '../../components/blog/PublicLeft';

/**
 * 我们的首页
 */
class StartPage extends React.Component{

    constructor(props){
        super(props)

        this.state={
            hotArticles:[],
            bingImages:[],
            imagePrefix:"https://blog.breakpoint.vip/biying",
            imageRequestUrl:"/HPImageArchive.aspx?format=js&idx=-1&n=8"
        }
    }

    /**
     *初始化我们的数据
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet("/blog/v1/topic/getTop3Topic",function(response){
            console.log(response)
            _this.setState({
                hotArticles:response
            })
        });

        FetchDoGet(this.state.imagePrefix+this.state.imageRequestUrl,function(response){
            console.log(response)
            _this.setState({
                bingImages:response
            })
        },{},false);



        // FetchDoGet("/blog/v1/topic/getTop3Topic",function(response){

        // })

    }

    render(){

        return(
            <BlogCommonBackGround
            // leftSpan={0}
            // rightSpan={24}
            // rightOffset={0}
            left={
                <PublicLeft/>
            }
            extra={
                <div>
                    <Row gutter={[16, 24]}>
                        <Col span={24}>
                            <Card className="start-page-welcome">
                                <h3>博主寄语</h3>
                                <p>首先，欢迎访问我的博客网站。</p>
                                <p>你是幸运的，访问了我的私人博客，在这里，你可以学到一些编码的技术。</p>
                                <p>该网站设计以及实现均有博主自己实现，有任何问题，或者交流学习，请联系博主。</p>
                            </Card>
                        </Col>
                    </Row>
                    <Row gutter={[16, 24]}>
                        <Col span={12}>
                            <Card title={"最新图片"}>

                            </Card>
                        </Col>
                        <Col span={12}>
                            <Card title={"作者名片"}>

                            </Card>
                        </Col>
                    </Row>
                    <Row gutter={[16, 24]}>
                        <Col span={24}>
                            <Card 
                            title={"最受欢迎的文章"}
                            extra={
                                <Button type="primary"
                                href="/tech-talk"
                                >阅读更多文章</Button>
                            }
                            bordered={false}>
                                <List
                                itemLayout="vertical"
                                size="default"
                                // grid={{ gutter: 16, column: 2}}
                                pagination={false}
                                dataSource={this.state.hotArticles}
                                renderItem={(item,index)=>(
                                    <List.Item
                                    attr={index}
                                    // key={item.topicTitle}
                                    actions={[
                                        <Breadcrumb
                                                style={{marginTop:"24px"}}
                                                separator={" "}>
                                                    <Breadcrumb.Item>
                                                        <span style={{marginRight: "8px"}}>
                                                            <LocalIconFont type="icon-information"/>&nbsp;
                                                            {item.nickName}
                                                        </span>
                                                    </Breadcrumb.Item>
                                                    <Breadcrumb.Item>
                                                        <span style={{marginRight: "8px"}}>
                                                            <LocalIconFont type="icon-see"/>&nbsp;
                                                            {item.seeCount}人已围观
                                                        </span>
                                                    </Breadcrumb.Item>
                                                    <Breadcrumb.Item>
                                                        <span style={{marginRight: "8px"}}>
                                                            <LocalIconFont type="icon-riqi2"/>&nbsp;
                                                            {item.gmtModified}
                                                        </span>
                                                    </Breadcrumb.Item>
                                                </Breadcrumb>
                                    ]}
                                    >

                                        <List.Item.Meta
                                            // avatar={
                                            //     <Avatar src="http://file.breakpoint.vip/picture/blog/4f6bbb5c-2404-484a-a9f1-062317bc840e.jpg" />
                                            // }
                                            title={
                                                <span>
                                                    <a style={{marginLeft:"8px"}} href={`/tech-article?topicId=`+item.topicId}>
                                                        <span style={{fontSize:"18px"}}>{item.topicTitle}</span>
                                                    </a>
                                                </span>
                                            }
                                            description={
                                                    <div>
                                                        <Tag color="blue">{item.topicCategory}</Tag>
                                                        {item.topicDesc}
                                                        <Button type="link"
                                                        href={`/tech-article?topicId=`+item.topicId}
                                                        >阅读更多</Button>
                                                    </div>
                                            }
                                            />
                                        </List.Item>   
                                )}
                                />

                            </Card>
                        </Col>
                    </Row>
                </div>
            }
            />
        )
    }
}

export default withRouter(StartPage)
