import React from 'react'
import {fetch as fetchPolyfill} from 'whatwg-fetch'
import { BrowserRouter as Router,Route,Link,Redirect, withRouter} from "react-router-dom";
import LoginMod from './../login/Signin'

import 'antd/dist/antd.css';
import { Button, notification,message,Modal } from 'antd';
import { ExclamationCircleOutlined } from '@ant-design/icons';
const { confirm } = Modal;

/**
 * 我们的基本的工具
 */
//const prefix="http://api.breakpoint.vip/";
const prefix="http://localhost:8090/";


const openNotificationWithIcon = type => {
    notification[type]({
      message: 'Notification Title',
      description:
        'This is the content of the notification. This is the content of the notification. This is the content of the notification.',
    });
};

/**
 * 我们的基本的提示操作
 * @param {string} type 
 * @param {string} content 
 */
export function MessageAlter(type,content){
    if("success"==type){
        message.success(content,2);
    }else if("error"==type){
        message.error(content,2)
    }else{
        message.warning(content,2);
    }
}


const  showConfirm=function(title,content) {
    confirm({
      title: title,
      icon: <ExclamationCircleOutlined />,
      content: content,
      cancelButtonProps: {
        disabled: true,
      },
      onOk() {
        console.log('OK');
        window.location.href="/login"
      },
      onCancel() {
        console.log('Cancel');
      },
    });
  }

  function showDeleteConfirm() {
    confirm({
      title: 'Are you sure delete this task?',
      icon: <ExclamationCircleOutlined />,
      content: 'Some descriptions',
      okText: 'Yes',
      okType: 'danger',
      cancelText: 'No',
      onOk() {
        console.log('OK');
      },
      onCancel() {
        console.log('Cancel');
      },
    });
  }



/**
 * 
 * @param {*} url  请求的url
 * @param {*} callback  回掉函数
 * @param {*} paramMap  请求参数
 */
export function FetchDoGet(url,callback,paramMap,isAlert){
    //let prex=prefix[1]
    console.log("prefx",prefix)
    let moreStr="?"
    if(null!=paramMap&&undefined!=paramMap){
        const keys=Object.keys(paramMap);
        for(var index in keys){
            {moreStr=moreStr+"&"+keys[index]+"="+paramMap[keys[index]]}
        }
    }

    const token=localStorage.getItem("user_token");
    moreStr=moreStr+"&token="+token
    fetch(prefix+url+moreStr,{
        method:"GET",
    }).then(function(response){
        return response.json()
    }).then(function(json){
        console.log(isAlert);
        if(undefined==isAlert||null==isAlert||isAlert){
            if(json.respCode===200){
                if(null!=callback&&undefined!=callback){
                    callback(json.data) 
                }
            }else if(json.respCode===300){
                localStorage.setItem("user_info",null)
                showConfirm("系统提示","您没有登陆")
                //alert(json.data)
                //openNotificationWithIcon('warning')
                 //window.location.href="/login"
            }else{
                //alert(json.data)
                message.warning(json.data);
                //openNotificationWithIcon('warning')
            }
        }else{
            callback(json) 
        }
       console.log(json)
    });
}

/**
 * post 的请求操作
 * @param {*} url  请求的路径
 * @param {*} callback  回掉的函数
 * @param {*} paramMap  请求的参数
 */
export function FetchDoPost(url,callback,paramMap){
    console.log("prefx",prefix)
    let data={}
    if(null!=paramMap&&undefined!=paramMap){
        data=paramMap
    }

    let moreStr="?"
    if(null!=paramMap&&undefined!=paramMap){
        const keys=Object.keys(paramMap);
        for(var index in keys){
            {moreStr=moreStr+"&"+keys[index]+"="+paramMap[keys[index]]}
        }
    }
    console.log("data",data);
    const token=localStorage.getItem("user_token");
    moreStr=moreStr+"&token="+token
    fetch(prefix+url+moreStr,{
        method:"POST",
        // mode: 'cors',
        // credentials: 'include',
        headers: {
            'Accept': 'application/json', 
            'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
          },
        body:JSON.stringify(data)
    }).then(function(response){
        return response.json()
    }).then(function(json){
        if(json.respCode==200){
            if(null!=callback&&undefined!=callback){
                callback(json.data)   
            }
        }else if(json.respCode==300){
            localStorage.setItem("user_info",null)
            showConfirm("系统提示","您没有登陆")
            //alert(json.data)
            //return <Redirect to="/login" />;

        }else{
            message.warning(json.data);
            //alert(json.data)
        }
       console.log(json)
    });

}
