 * 我们的登陆的基本操作
 */
class LoginMod extends React.Component{
  constructor(props){
    super(props)
    this.state={
      src: this.props.src ? this.props.src : '',
      verifyCodeKey:'',
      open:false,
      message:'测试',
      severity:"success"
    }
    this.refreshVerifyCode=this.refreshVerifyCode.bind(this)
    this.preSubmit=this.preSubmit.bind(this)
    this.callBackExe=this.callBackExe.bind(this)
  }

  refreshVerifyCode(){
    const _this=this;
    FetchDoGet("/block/v1/image/getVerifyCode",function(response){
      _this.setState({
        src: response.verifyCode,
        verifyCodeKey:response.verifyCodeKey,
        isLogin:false
    })
      console.log("response",response);
    });
  }

  preSubmit(e) {  

    const _this=this;
    console.log(e)
    //e.target;
    console.log(e.target);

    let requestData={
      verifyCodeKey:this.state.verifyCodeKey,
      verifyCode:$("#verifyCode").val()
    }

    const username=$("#username").val();
    const password=$("#password").val();

    if(undefined==username||null==username||username.length==0){
      alert("请求输人用户名");
      //e.preventDefault();
      e.preventDefault();
      return;

    }

    if(undefined==password||null==password||username.password==0){
      alert("请求输人密码");
      e.preventDefault();
      return;

    }

    FetchDoPost("/block/v1/sys_user/login/"+username+"/"+password,function(response) {
      localStorage.setItem("user_token",response.token)
      _this.props.history.push('/private');
      console.log(response);
    },requestData);
    /**
     * 阻止提交我们的提交
     */
    e.preventDefault();
  }

  componentDidMount(){
    /**
     * 存储我们一些的基本的信息
     */
    const _this=this;

    /**
     * 校验我们是否登陆了 根据登陆的用户获取我们的菜单操作
     */
     FetchDoGet("http://api.breakpoint.vip/block/v1/menu/getMenuByLoginUser",function(response) {
       //this.props.his
       _this.props.history.push('/private');
     });



    FetchDoGet("/block/v1/menu/getMenuByLoginUser",function(response) {
      if(response.respCode===300){
        _this.setState({
          isLogin:false
        })
      }
      console.log("sss",response);
     },{},false);

    /**
     * 获取我们的验证码
     */
    FetchDoGet("/block/v1/image/getVerifyCode",function(response){
      _this.setState({
        src: response.verifyCode,
        verifyCodeKey:response.verifyCodeKey,
    })
    console.log("response",response);
    },{'key1':'value',"verifyCodeKey":"ssddd"});
  }

  callBackExe(param){
    this.setState({
      open:false,
      message:'',
    })
    console.log(param);
  }

  render(){

    //return <Redirect to='/' />;

    const {classes} = this.props;

    let {src,open,message,severity} = this.state;

    return(
<Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
      <div className={classes.title}>基于区块链的光网络建设管理平台</div>
        {/* <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar> */}
        {/* <Typography component="h1" variant="h5">
          Sign in
        </Typography> */}
        <form className={classes.form} noValidate onSubmit={this.preSubmit}>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="username"
            label="用户名"
            name="username"
            autoComplete="username"
            autoFocus
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="密码"
            type="password"
            id="password"
            autoComplete="current-password"
          />
          <div>
          <TextField id="verifyCode"
           label="验证码"
           required
           fullWidth
            variant="outlined" />
          <img id="" src={src} className={classes.verifyCode} onClick={this.refreshVerifyCode}/>
          </div>

          {/* <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          /> */}
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            登陆
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              <Link href="#" variant="body2">
                {"Don't have an account? Sign Up"}
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={8}>
        <CopyRight />
      </Box>
      <Dialog open={open} message={message} severity={severity} callBack={this.callBackExe}/>
    </Container>
    )
  }
}

export default withStyles(style)(withRouter(LoginMod));
