import React from 'react' 
import { withRouter } from 'react-router-dom'
import { Card } from 'antd'

/**
 * 每日一句
 */
class EvedaySay extends React.Component{

    constructor(props){
        super(props)
        this.state={

            oneSay:{}

        }
    }

    /**
     * 初始化我们的数据
     */
    componentDidMount(){
        const _this=this;
        fetch("https://api.ooopn.com/ciba/api.php?type=json")
        .then((response)=>{
            return response.json()
        })
        .then((json)=>{
            console.log(json)
            _this.setState({
                oneSay:json
            })
        })

    }

    render(){

        return(
           <div>
               <img src={this.state.oneSay.imgurl}/>
           </div>
        )
    }

}
export default withRouter(EvedaySay)
