import React from 'react'
import {fetch as fetchPolyfill} from 'whatwg-fetch'


/**
 * 我们的基本的工具
 */

// parseJSON=function(response){
//     return response.json()
// }

export function FetchDoGet(url,callback,paramMap){

    const moreStr="?"

    if(null!=paramMap&&undefined!=paramMap){
        const keys=Object.keys(paramMap);
        for(var key in keys){
            {moreStr=moreStr+"&"+key+"="+paramMap[keys]}
        }
    }

    fetch(url+moreStr,{
        method:"GET",
    }).then(function(response){
        return response.json()
    }).then(function(json){
        if(json.respCode==200){
            if(null!=callback&&undefined!=callback){
                callback(json.data)   
            }
        }else if(json.respCode==300){
            alert(json.data)
        }else{
            alert(json.data)
        }
       console.log(json)
    });
}
