import React from 'react'
import './UserInfo.css'


import './../commons/layoutcom.css'
import HeaderPrivate from './../components/HeaderPrivate'
import Bottom from './../components/Bottom'
import { withRouter } from 'react-router-dom'
import 'antd/dist/antd.css';
import { Layout,Card,Button,Divider,Table} from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
} from '@ant-design/icons';
import MenuComponent from './../components/MenuCom'
import {FetchDoGet,FetchDoPost} from './../commons/Utils'

const { Header, Sider, Content} = Layout;

const gridStyle = {
    width: '50%',
    textAlign: 'center',
};


const columns = [
    {
        title: '用户名',
        width: 100,
        dataIndex: 'userName',
        key: 'userName',
        fixed: 'left',
    },
    {
        title: '昵称',
        width: 100,
        dataIndex: 'userNickName',
        key: 'userNickName',
        fixed: 'left',
    },
    {
        title: '手机号',
        width: 100,
        dataIndex: 'userPhone',
        key: 'userPhone',
    },
    {
        title: '邮箱',
        width: 100,
        dataIndex: 'userEmail',
        key: 'userEmail',
    },
    {
        title: '部门',
        width: 100,
        dataIndex: 'departName',
        key: 'departName',
    },
    {
        title: '角色',
        width: 100,
        dataIndex: 'roleName',
        key: 'roleName',
    },
    {
        title: '创建时间',
        width: 100,
        dataIndex: 'gmtCreate',
        key: 'gmtCreate',
    },
    {
        title: '操作',
        key: 'operation',
        fixed: 'right',
        width: 100,
        render: () => <a>action</a>,
    },
];


class UserInfo extends React.Component{

    constructor(props){
        super(props)
        this.state={
            collapsed: true,
            users:{}
        }
    }

    toggle = () => {
        this.setState({
          collapsed: !this.state.collapsed,
        });
    };

    /**
     * 初始化基本的信息
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet("/block/v1/sys_user/selectAllUsers",function(response){
            console.log("selectAllUsers",response);
            _this.setState({
                users:response
            })
        });

    }

    render(){
        return(
            <div>
                <HeaderPrivate name="zhaoligang"/>
                    <Layout className="layout-header">
                        <Sider theme="light" trigger={null} collapsible collapsed={this.state.collapsed}>
                        <div className="logo" />
                        <MenuComponent/>
                        </Sider>
                        <Layout className="site-layout">
                        <Header className="site-layout-background" style={{ padding: 0 }}>
                            {React.createElement(this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
                            className: 'trigger',
                            onClick: this.toggle,
                            })}
                        </Header>
                        <Content
                            className="site-layout-background"
                            style={{
                            margin: '24px 16px',
                            padding: 24,
                            minHeight: 580,
                            }}
                        >
                            <Card style={{width:"100%"}} 
                            bordered={false}
                            hoverable={true}
                            title="用户信息列表"
                            extra={
                                <div>
                                    <Button type="primary" style={{float:"right"}}>添加用户</Button>
                                    <Button type="primary" style={{float:"right"}}>查询</Button>  
                                </div>
                            }
                            >

                                <Table columns={columns} dataSource={this.state.users.data} scroll={{ x: 1500, y: 300 }} />,

                            </Card>

                            <Divider dashed={true}/>

                            <Card style={{width:"100%"}} 
                            bordered={false}
                            hoverable={true}
                            title="个人资产信息"
                            extra={<Button type="primary" style={{float:"right"}}>查看详情</Button>}
                            >

                            </Card>
                        </Content>
                        </Layout>
                    </Layout>
                <Bottom/>
            </div>
        )
    }
}
export default UserInfo
