//     <Router>
//             <Route path={`/private`} component={PrivateZone}></Route>
//             <Route path={`/login`} component={LoginMod}></Route>
//      </Router>
//   );
// }


const mapStateToProps = (state, ownProps) => {
  return { token: state.token }
}

export default (App);
