import React from 'react'
import './Album.css'
import { withRouter } from 'react-router-dom'
import {Row,Col, Card,List,Pagination,Modal,Carousel} from 'antd'
import BlogCommonBackGround from '../../components/blog/BlogCommonBackGround'
import { FetchDoGet } from '../../commons/Utils'
import { EnvironmentOutlined } from '@ant-design/icons';



const getter=[16,24];
const colSpan=6;
/**
 * 我们的相册的页
 */
class Album extends React.Component{

    constructor(props){
        super(props)

        this.state={
            requestUrl:"/blog/v1/album/selectPhotos",
            photoPageInfo:{},
            photos:[],
            first:{}
        }
    }

    /**
     * 初始化我们的基本的数据
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet(this.state.requestUrl,(response)=>{
            console.log(response)
            this.setState({
                photoPageInfo:response,
                photos:response.data,
                first:response.data[0],
                visible: false
            })
        })

    }

    render(){

        return(
            <BlogCommonBackGround
            leftSpan={0}
            rightSpan={24}
            extra={
                <div>
                    {/* <Row getter={[16, 16]}>
                        <Col span={24}>
                            <Card 
                                hoverable
                                cover={<img alt="example" src={this.state.first.aPhoto}/>}
                                // title={item.aLocaltion}
                                >
                                <Card.Meta title={
                                    <span>
                                        <EnvironmentOutlined />
                                        {` `+this.state.first.aLocaltion}
                                    </span>

                                    } description={
                                    <div>
                                        <span style={{fontWeight:"bold",color:"black"}}>{this.state.first.gmtUpdate+` `}</span>
                                        {this.state.first.aMsg}
                                    </div>
                                } />
                                </Card>
                        </Col>
                     </Row> */}
                     <Row gutter={[16, 16]} justify="space-around" align="middle">
                         {this.state.photos.map((item,index)=>{
                             return(
                                <Col span={4}>
                                    <Card
                                    onClick={(index)=>{
                                        const _this=this;
                                        _this.setState({
                                            visible:true
                                        })
                                    }}
                                    hoverable
                                    cover={<img alt="example" src={item.aPhoto}/>}
                                    // title={item.aLocaltion}
                                    >
                                    <Card.Meta title={
                                        <span>
                                            <EnvironmentOutlined />
                                            {` `+item.aLocaltion}
                                        </span>

                                        } description={
                                        <div>
                                            <span style={{fontWeight:"bold",color:"black"}}>{item.gmtUpdate+` `}</span>
                                            {item.aMsg}
                                        </div>
                                    } />
                                    </Card>
                                </Col>
                             )
                         })}
                     </Row>
                     <Row gutter={[16, 16]} justify="space-around" align="middle">
                         <Pagination
                         current={this.state.photoPageInfo.currentPage}
                         defaultCurrent={this.state.photoPageInfo.currentPage}
                         defaultPageSize={this.state.photoPageInfo.pageSize}
                        //  pageSize={this.state.photoPageInfo.pageSize}
                        total={this.state.photoPageInfo.totalCount}
                         showQuickJumper={true}
                         showTotal={(total, range) => `当前展示：${range[0]}-${range[1]}图片  一共有${total}张图片`}
                         onChange={(page, pageSize)=>{

                         }}

                         />
                     </Row>
                        {/* <List
                            grid={{ gutter: [16, 24], column: 2,align:"middle" }}
                            dataSource={this.state.photos}
                            renderItem={item => (
                            <List.Item>
                                <Card 
                                hoverable
                                cover={<img alt="example" src={item.aPhoto}/>}
                                // title={item.aLocaltion}
                                >
                                <Card.Meta title={
                                    <span>
                                        <EnvironmentOutlined />
                                        {` `+item.aLocaltion}
                                    </span>

                                    } description={
                                    <div>
                                        <span style={{fontWeight:"bold",color:"black"}}>{item.gmtUpdate+` `}</span>
                                        {item.aMsg}
                                    </div>
                                } />
                                </Card>
                            </List.Item>
                            )}
                        /> */}

                        <Modal
                        // title="Basic Modal"
                        style={{width:"1200px"}}
                        width={1000}
                        visible={this.state.visible}
                        onOk={this.handleOk}
                        onCancel={()=>{
                            const _this=this;
                            _this.setState({
                                visible:false
                            })
                        }}
                        okButtonProps={{ disabled: true }}
                        cancelButtonProps={{ disabled: true }}
                        footer={null}
                        >
                            <Carousel 
                            autoplay
                            effect="fade">
                            {this.state.photos.map((item,index)=>{
                             return(
                                <div style={{width:"100%"}}>
                                    <img alt="example" src={item.aPhoto}/>
                                </div>
                                )
                            })}
                            </Carousel>
                        </Modal>

                </div>
            }
            />
        )
    }
}

export default withRouter(Album)
