import React from 'react'
import './TechTalk.css'
import { withRouter } from 'react-router-dom'
import BlogCommonBackGround from '../../components/blog/BlogCommonBackGround'
import { FetchDoGet } from '../../commons/Utils'
import { List, Avatar,Pagination,Button } from 'antd';
import { MessageOutlined, LikeOutlined, StarOutlined ,HeartOutlined} from '@ant-design/icons';


const IconText = ({ icon, text }) => (
    <span>
      {React.createElement(icon, { style: { marginRight: 8 } })}
      {text}
    </span>
  );

/**
 * 我们的技术杂谈的页面
 */
class TechTalk extends React.Component{

    constructor(props){
        super(props)
        this.state={
            requestUrl:"/blog/v1/topic/getAllTopic",
            topicList:[],
            pageInfo:{},
            initLoading: true,
            loading: false,
        }
    }

    /**
     * 初始化我们的数据
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet(this.state.requestUrl,function(response){
            console.log(response)
            _this.setState({
                initLoading: false,
                topicList:response.data,
                pageInfo:response,
            })
        });
    }

    render(){


        return(
            <BlogCommonBackGround
            extra={
                <div>
                    <List
                    itemLayout="vertical"
                    size="large"
                    pagination={false}
                    dataSource={this.state.topicList}
                    footer={
                        <div>
                          <b>ant design</b> footer part
                        </div>
                    }
                    renderItem={(item)=>(
                        <List.Item
                        key={item.topicTitle}
                        actions={[
                            <IconText icon={HeartOutlined} text="156" key="list-vertical-star-o" />,
                            <IconText icon={LikeOutlined} text="156" key="list-vertical-like-o" />,
                            <IconText icon={MessageOutlined} text="2" key="list-vertical-message" />,
                          ]}

                          extra={
                            <img
                              width={272}
                              alt="logo"
                              src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
                            />
                          }
                        >
                            <List.Item.Meta
                                avatar={<Avatar src={item.photoPath} />}
                                title={<a href={item.photoPath}>{item.topicTitle}</a>}
                                description={item.topicDesc}
                                />
                                {item.topicDesc}
                            </List.Item>   
                      )}
                    />
                    <Pagination 
                    showQuickJumper
                    showTotal={(total, range)=>{
                        return `${range[0]}-${range[1]} of ${total} items`
                    }}
                    current={this.state.pageInfo.currentPage}
                    defaultPageSize={this.state.pageInfo.pageSize}
                    defaultCurrent={this.state.pageInfo.currentPage} 
                    total={this.state.pageInfo.totalCount} 
                    onChange={(page, pageSize)=>{
                        const _this=this;
                        FetchDoGet(this.state.requestUrl,function(response){
                            console.log(response)
                            _this.setState({
                                topicList:response.data,
                                pageInfo:response,
                            })
                        },{
                            currentPage:page,
                            pageSize:pageSize
                        });
                    }} />
                </div>
            }
            />
        )
    }

}

export default withRouter(TechTalk)
