import React from 'react'
import './UserInfo.css'

/**
 * 基本的公共的引入
 */
import './../commons/layoutcom.css'
import HeaderPrivate from '../components/HeaderPrivate'
import Bottom from '../components/Bottom'
import { withRouter } from 'react-router-dom'
import 'antd/dist/antd.css';
import { Layout,Card,Button,Divider,Avatar} from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
} from '@ant-design/icons';
import MenuComponent from '../components/MenuCom'
import {FetchDoGet,FetchDoPost} from '../commons/Utils'

//end

const { Header, Sider, Content} = Layout;


const gridStyle = {
    width: '50%',
    textAlign: 'center',
};


/**
 * 个人基本信息页展示
 */
class UserInfo extends React.Component{
    constructor(props){
        super(props)
        this.state={
            userData:null,
            collapsed: false,
            user:{},
            role:{},
            other:{}
        }
    }


    // state = {
    //         collapsed: false,
    //         userData:null
    // };

     toggle = () => {
        this.setState({
          collapsed: !this.state.collapsed,
        });
    };
      /**
       * 初始化我们的基本的数据操作
       */
      componentDidMount(){
        const _this=this;
            FetchDoGet("/block/v1/sys_user/getCurrentLoginUser",function(response){
                console.log("userinfo",response);
                _this.setState({
                    userData:response,
                    user:response.tbSystemUser,
                    role:response.tbSystemRole,
                    other:response.userOtherMessageInfo
                })
            });
      }


      render() {

        let {user,role,other}=this.state;

        return ( 
            <div>
                <HeaderPrivate name="zhaoligang" callback={this.toggle}/>
                    <Layout className="layout-header">
                        <Sider theme="light" trigger={null} collapsible collapsed={this.state.collapsed}>
                        <div className="logo" />
                        <MenuComponent/>
                        </Sider>
                        <Layout className="site-layout">
                        {/* <Header className="site-layout-background" style={{ padding: 0 }}>
                            {React.createElement(this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
                            className: 'trigger',
                            onClick: this.toggle,
                            })}
                        </Header> */}
                        <Content
                            className="site-layout-background"
                            style={{
                                margin: '5px 5px',
                                padding: 24,
                                minHeight: 580,
                            }}
                        >
                            <Card style={{width:"100%"}} 
                            bordered={false}
                            hoverable={true}
                            title="用户的个人信息"
                            extra={<Button type="primary" style={{float:"right"}}>编辑个人信息</Button>}
                            >
                                <Card style={{width:"80%",float:"left",textAlign:"center"}}
                                bordered={false}
                                >
                                    <Card.Grid hoverable={false} style={gridStyle}>用户名：{user.userName}</Card.Grid>
                                    <Card.Grid hoverable={false} style={gridStyle}>昵称：{user.userNickName}</Card.Grid>
                                    <Card.Grid hoverable={false} style={gridStyle}>手机：{user.userPhone}</Card.Grid>
                                    <Card.Grid hoverable={false} style={gridStyle}>邮件：{user.userEmail}</Card.Grid>
                                    <Card.Grid hoverable={false} style={gridStyle}>角色：{role.roleName}</Card.Grid>
                                    <Card.Grid hoverable={false} style={gridStyle}>其他信息：{other.departLeader?"部门领导人":""}</Card.Grid>
                                </Card>
                                <Card style={{width:"20%",float:"right"}}
                                // cover={<img src={user.userPhoto}/>}
                                >   
                                <Avatar src={user.userPhoto}
                                size="large"
                                style={{width:"60px",height:"60px"}}
                                />

                                    <Card.Meta title={user.userName} description={user.userPhone} />
                                </Card>
                            </Card>

                            <Divider dashed={true}/>


                            <Card style={{width:"100%"}} 
                            bordered={false}
                            hoverable={true}
                            title="个人资产信息"
                            extra={<Button type="primary" style={{float:"right"}}>查看详情</Button>}
                            >

                            </Card>
                        </Content>
                        </Layout>
                    </Layout>
                <Bottom/>
            </div>
        );
      }
}//end
export default withRouter(UserInfo)
