import React from 'react'
import './EditTopic.css'
import { withRouter } from 'react-router-dom'
import PrivateZone from './PrivateZone'
import Editor from 'for-editor'
import { Input, Row ,Col, Card} from 'antd'
import { TextField } from '@material-ui/core'

/**
 * 编辑我们的文章
 */
class EditTopic extends React.Component{

    constructor(props){
        super(props)
        this.state={
            markdownvalue:""
        }
    }

    /**
     * 初始化我们的数据
     */
    componentDidMount(){

    }
    render(){

        return(
            <PrivateZone
            leftSpan={0}
            rightSpan={24}
            extra={
                <Card>
                    <Row gutter={[16,16]}>
                        <Col span={24}>
                            <TextField 
                            fullWidth
                            id="outlined-basic" 
                            label="文章标题" 
                            variant="outlined" />
                        </Col>
                        <Col span={24}>
                            <Editor
                            style={{width:"100%"}}
                            placeholder={"请输入文章的信息"}
                            value={this.state.markdownvalue} onChange={(value) =>{
                                console.log(value)
                                this.setState({
                                    markdownvalue:value
                                })
                            }} />
                        </Col>
                    </Row>
                    <Input/>

                </Card>
            }
            />
        )
    }

}

export default withRouter(EditTopic)
