import React from 'react'

//基本的公共导入模块 start
import './../commons/layoutcom.css'
import HeaderPrivate from './../components/HeaderPrivate'
import Bottom from './../components/Bottom'
import { withRouter } from 'react-router-dom'
import 'antd/dist/antd.css';
import { Layout} from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
} from '@ant-design/icons';
import MenuComponent from './../components/MenuCom'
import {FetchDoGet,FetchDoPost} from './../commons/Utils'

//基本的公共导入模块 end 

//定义常量
const { Header, Sider, Content} = Layout;
/**
 * 我们后台的公共模板
 * 用于后台的所有的页面
 */
class LocalBackGround extends React.Component{
    //构造函数
    constructor(props){
        super(props)
        this.state={
            collapsed: false,
            userData:{}//用户的基本信息
        }
    }
    toggle = () => {
        this.setState({
          collapsed: !this.state.collapsed,
        });
    };
    //初始化我们的数据
    componentDidMount(){
        const _this=this;
        //获取用户的登陆的信息
        FetchDoGet("/block/v1/sys_user/getCurrentLoginUser",function(response){
            _this.setState({
                userData:response 
            })
        });  
    }
    //执行渲染我们的组件
    render(){
        let {userData}=this.state;
        return( 
            <div>
                <HeaderPrivate name="zhaoligang" 
                callback={this.toggle} 
                avatar={userData.tbSystemUser.userPhoto}
                />
                    <Layout className="layout-header">
                        <Sider theme="light" trigger={null} collapsible collapsed={this.state.collapsed}>
                        <div className="logo" />
                        <MenuComponent/>
                        </Sider>
                        <Layout className="site-layout">
                        {/* <Header className="site-layout-background" style={{ padding: 0 }}>
                            {React.createElement(this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
                            className: 'trigger',
                            onClick: this.toggle,
                            })}
                        </Header> */}
                        <Content
                            className="site-layout-background"
                            style={{
                                margin: '4px 5px 0px 3px',
                                padding: 10,
                                minHeight: 680,
                            }}
                        >
                            {/* //定义我们自己的组件 */}
                         {this.props.content}
                        </Content>
                        </Layout>
                    </Layout>
                <Bottom/>
            </div>
        )//end return

    }//end render
}

export default withRouter(LocalBackGround)
