import React from 'react'
import {fetch as fetchPolyfill} from 'whatwg-fetch'


/**
 * 我们的基本的工具
 */

/**
 * 
 * @param {*} url  请求的url
 * @param {*} callback  回掉函数
 * @param {*} paramMap  请求参数
 */
export function FetchDoGet(url,callback,paramMap){
    let moreStr="?"
    if(null!=paramMap&&undefined!=paramMap){
        const keys=Object.keys(paramMap);
        for(var index in keys){
            {moreStr=moreStr+"&"+keys[index]+"="+paramMap[keys[index]]}
        }
    }
    fetch(url+moreStr,{
        method:"GET",
    }).then(function(response){
        return response.json()
    }).then(function(json){
        if(json.respCode==200){
            if(null!=callback&&undefined!=callback){
                callback(json.data)   
            }
        }else if(json.respCode==300){
            alert(json.data)
        }else{
            alert(json.data)
        }
       console.log(json)
    });
}

/**
 * post 的请求操作
 * @param {*} url  请求的路径
 * @param {*} callback  回掉的函数
 * @param {*} paramMap  请求的参数
 */
export function FetchDoPost(url,callback,paramMap){
    let data={}
    if(null!=paramMap&&undefined!=paramMap){
        data=paramMap
    }
    fetch(url,{
        method:"POST",
        // mode: 'cors',
        // credentials: 'include',
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
          },
        body:JSON.stringify(data)
    }).then(function(response){
        return response.json()
    }).then(function(json){
        if(json.respCode==200){
            if(null!=callback&&undefined!=callback){
                callback(json.data)   
            }
        }else if(json.respCode==300){
            alert(json.data)
        }else{
            alert(json.data)
        }
       console.log(json)
    });

}
