import React from 'react'
import './ArticleBar.css'
import { withRouter } from 'react-router-dom'
import echarts from 'echarts/lib/echarts';
import 'echarts/lib/chart/pie';
import 'echarts/lib/component/tooltip';
import 'echarts/lib/component/title';
import 'echarts/lib/component/legend';
import { FetchDoGet } from '../../commons/Utils';
// import 'echarts/lib/component/toolbox';
// import 'echarts/lib/component/markPoint';
// import 'echarts/lib/component/markLine';

/**
 * 统计我们的文章的基本谢信息
 */
class ArticleBar extends React.Component{

    constructor(props){
        super(props)
        this.state={
            requestUrl:"/blog/v1/statistic/statisticTopic",
            data:[]

        }
    }

    /**
     * 初始化我们的数据
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet(this.state.requestUrl,(response)=>{
            this.setState({
            data:response
            })
        })
        //初始化
        var myChart = echarts.init(document.getElementById('main'));
        myChart.setOption({
            title: {
                text: '共计200篇',
                subtext: '虚构数据',
                left: 'center'
            },
            tooltip: {
                trigger: 'item',
                formatter: '{a} <br/>{b}: {c} ({d}%)'
            },
            legend: {
                orient: 'vertical',
                left: 10,
                data: ['Java', 'JavaScript', 'Spring', 'Mybatis', 'Hibernate',
                'Other', 'database', 'algorithm', 'life']
            },
            series: [
                {
                    name: '文章统计',
                    type: 'pie',
                    radius: ['50%', '70%'],
                    avoidLabelOverlap: false,
                    label: {
                        show: false,
                        position: 'center'
                    },
                    emphasis: {
                        label: {
                            show: true,
                            fontSize: '30',
                            fontWeight: 'bold'
                        }
                    },
                    labelLine: {
                        show: false
                    },
                    data: this.state.data
                }
            ]
        })

    }

    render() {
        return (
            <div id="main" style={{ width: '100%',height:"300px"}}></div>
        );
    }

}

export default withRouter(ArticleBar)
