import React from 'react'
import './TechTalk.css'
import { withRouter } from 'react-router-dom'
import BlogCommonBackGround from '../../components/blog/BlogCommonBackGround'
import { FetchDoGet } from '../../commons/Utils'
import { List, Avatar } from 'antd';
import { MessageOutlined, LikeOutlined, StarOutlined } from '@ant-design/icons';


const IconText = ({ icon, text }) => (
    <span>
      {React.createElement(icon, { style: { marginRight: 8 } })}
      {text}
    </span>
  );

/**
 * 我们的技术杂谈的页面
 */
class TechTalk extends React.Component{

    constructor(props){
        super(props)
        this.state={
            topicList:[],
            pageInfo:{}
        }
    }

    /**
     * 初始化我们的数据
     */
    componentDidMount(){
        const _this=this;
        FetchDoGet("/blog/v1/topic/getAllTopic",function(response){
            console.log(response)
            _this.setState({
                topicList:response.data,
                pageInfo:response
            })
        });
    }

    render(){

        let {topicList}=this.state;

        return(
            <BlogCommonBackGround
            extra={
                <div>
                    <List
                    itemLayout="vertical"
                    size="large"
                    pagination={{
                        onChange: page => {
                          console.log(page);
                        },
                        pageSize: 3,
                      }}
                      dataSource={this.state.topicList}

                      footer={
                        <div>
                          <b>ant design</b> footer part
                        </div>
                      }
                      renderItem={(item)=>(
                        <List.Item
                        key={item.topicTitle}
                        actions={[
                            <IconText icon={StarOutlined} text="156" key="list-vertical-star-o" />,
                            <IconText icon={LikeOutlined} text="156" key="list-vertical-like-o" />,
                            <IconText icon={MessageOutlined} text="2" key="list-vertical-message" />,
                          ]}

                          extra={
                            <img
                              width={272}
                              alt="logo"
                              src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
                            />
                          }
                        >
                            <List.Item.Meta
                                avatar={<Avatar src={item.photoPath} />}
                                title={<a href={item.photoPath}>{item.topicTitle}</a>}
                                description={item.topicDesc}
                                />
                                {item.topicDesc}
                            </List.Item>   
                      )}
                    />
                </div>
            }
            />
        )
    }

}

export default withRouter(TechTalk)
