import React from 'react'
import LocalBackGround from './../commons/LocalBackGround'
import {FetchDoGet,FetchDoPost,Prefix, FetchDoUploadFile} from './../commons/Utils'
/**
 * 基本的公共的引入
 */
import Bottom from './../components/Bottom'
import { withRouter } from 'react-router-dom'
import 'antd/dist/antd.css';
import { Layout,Card,Button,Divider,Tabs, PageHeader,Avatar,Upload} from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  UploadOutlined
} from '@ant-design/icons';
//end


const itemCardStyle={
    width:"50%",
    float:"left"
}

const { TabPane } = Tabs;

const requestUrl=Prefix+'/block/v1/oss/uploadFileToOss'

/**
 * 我们的个人主页
 */
class EditUserInfo extends React.Component{
    constructor(props){
        super(props)
        this.state={
            userData:null,
            user:{},
            role:{},
            other:{}
        }
    }

    /**
     * 初始化我们的数据
     */
    componentDidMount(){
        const _this=this;
        /**
         * 获取当前登陆的用户
         */
        FetchDoGet("/block/v1/sys_user/getCurrentLoginUser",function(response){
            _this.setState({
                userData:response,
                user:response.tbSystemUser,
                role:response.tbSystemRole,
                other:response.userOtherMessageInfo
            })
        });

    }

    render(){
        let history=this.props.history;
        return(
            <LocalBackGround
            onBack={()=>{history.go(-1)}}
             content={
                <div>
                    <Card style={{width:"100%"}}>
                        <Tabs tabPosition="left"
                        size="large"
                        type="line"
                        >
                            <TabPane tab="基本设置" key="1">
                                <PageHeader title={"基本设置"}>
                                    <Card style={{width:"100%"}}
                                    bordered={false}
                                    >
                                        <Card style={{width:"50%",float:"left"}}
                                        bordered={false}
                                        >


                                        </Card>
                                        <Card style={{width:"50%",float:"right",textAlign:"center"}}
                                        bordered={false}
                                        >
                                            <Avatar size="large"
                                                style={{height:"80px",width:"80px",clear:"both"}}
                                                src={this.state.user.userPhoto}
                                            />
                                            <br/>
                                            <br/>
                                            <Upload 
                                            showUploadList={false}
                                            customRequest={(option)=>{
                                                const formData = new FormData();
                                                formData.append('file',option.file);
                                                //上传文件
                                                FetchDoUploadFile(null,formData,function(response){
                                                    console.log(response)
                                                })
                                            }}
                                            >
                                                <Button>
                                                <UploadOutlined /> 上传头像
                                                </Button>
                                            </Upload>

                                        </Card>
                                    </Card>
                                </PageHeader>  
                            </TabPane>
                            <TabPane tab="安全设置" key="2">
                                <PageHeader title={"安全设置"}>

                                </PageHeader>
                            </TabPane>
                        </Tabs>
                    </Card>
                </div>
            }/>
        )
    }
}

export default  withRouter(EditUserInfo)
