import React from 'react'
import './LeftArticleTitle.css'
import { withRouter } from 'react-router-dom'

import './../../store/store'
import store from './../../store/store'
import { connect } from 'react-redux'

/**
 * 文章的左标题
 */
class LeftArticleTitle extends React.Component{

    // 构造方法
    constructor(props){
        super(props)
        this.state={
            articleTitles:[]
        }
    }

    componentDidMount(){
        let articleTitles=this.props.articleTitles;
        this.setState({
            articleTitles:articleTitles
        })
    }

    render(){
        let articleTitles=this.state.articleTitles;
        console.log(articleTitles)
        return(
            <div>
                {
                    articleTitles.map((item,index)=>{
                        console.log(item)
                    })
                }
            </div>
        )
    }

}

const mapStateToProps = (state, ownProps) => {
    //console.log("mapStateToProps",state)
    return { articleTitles: state.articleTitles }
}

export default connect(mapStateToProps)(LeftArticleTitle)
