import React, { PureComponent } from "react";
import PropTypes from "prop-types";
import {Row} from 'antd'
import { PrismLight as SyntaxHighlighter } from "react-syntax-highlighter";
// 设置高亮样式
import { tomorrow,vs,funky,ghcolors,
  duotoneEarth,
  prism,
  atomDark ,coy,duotoneDark} from "react-syntax-highlighter/dist/esm/styles/prism";
// 设置高亮的语言
import { jsx, javascript, sass, scss,nginx, } from "react-syntax-highlighter/dist/esm/languages/prism";

class CodeBlock extends PureComponent {
  static propTypes = {
    value: PropTypes.string.isRequired,
    language: PropTypes.string
  };

  static defaultProps = {
    language: null
  };

  componentWillMount() {
    // 注册要高亮的语法，
    // 注意：如果不设置打包后供第三方使用是不起作用的
    SyntaxHighlighter.registerLanguage("jsx", jsx);
    SyntaxHighlighter.registerLanguage("javascript", javascript);
    // SyntaxHighlighter.registerLanguage("pre", nginx);
  }

  render() {
    const { language, value } = this.props;
    return (
      <Row style={{width:"100%",textAlign:"left",clear:"both",display:"inline-block"}}>
          <figure className="highlight">
                  <SyntaxHighlighter language={language} style={prism}>
                    {value}
                  </SyntaxHighlighter>
                </figure>
      </Row>

    );
  }
}

export default CodeBlock;
