import React from 'react'

import {Menu,Icon} from 'antd';
import 'antd/dist/antd.css'; 
//请求的基本操作
import {FetchDoGet,FetchDoPost} from './../commons/Utils'
import {Link, withRouter} from 'react-router-dom'
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  UserOutlined,
  VideoCameraOutlined,
  UploadOutlined,
} from '@ant-design/icons';

import LocalIconFont from './../components/LocalIconFont'

const SubMenu = Menu.SubMenu;
const MenuItemGroup = Menu.ItemGroup;


/**
 * 我们的menu的组件
 */
class MenuComponent extends React.Component{
    constructor(props){
        super(props)
        this.state={
            current:1,
            parentMenu:[]
        }
        this.menuOnOpenHandle=this.menuOnOpenHandle.bind(this)
        this.handleClick=this.handleClick.bind(this)
        this.subMenuClick=this.subMenuClick.bind(this)
    }
    componentDidMount(){
        const _this=this;
        FetchDoGet("block/v1/menu/getMenuByLoginUser",function (response) {
            _this.setState({
                parentMenu:response
            })
            console.log(response);

        });
    }

    getInitialState() {
        return {
          current: '1',
        };
      }
      handleClick(e) {
        console.log('click ', e);
        this.setState({
          current: e.key,
        });
      }

      menuOnOpenHandle(e){
          console.log("menuOnOpenHandle",e)
      }

      subMenuClick(e){
        console.log("subMenuClick",e)
        const _this=this;
        let {parentMenu}=_this.state;
        // FetchDoGet("block/v1/menu/getMenuByLoginUser",function (response) {
        //         //item[item.menuId]=response
        //         console.log(response);

        //         console.log("parentMenu",parentMenu);

